update.foreach:
UPDATE
operation_info
SET
dayst = #{value.dayst}, opercom = #{value.opercom}, opercar = #{value.opercar}, operid = #{value.operid}, atlm = #{value.atlm}
WHERE
operday = #{value.operday}
AND
opernum = #{value.opernum}
AND
operno = #{value.operno}
AND
opertype = #{value.opertype}

mapper.select:
SELECT
operseq, opernum, rsvt, operday, operno, opercom, opercar, operid, atlm, opertype, operconfirm, opertrash, IFNULL((SELECT vehicleinfo.vehicle FROM vehicleinfo WHERE vehicleinfo.carnumber = operation_info.opercar), operation_info.opercar) AS vehicle, IFNULL((SELECT employeeinfo.name FROM employeeinfo WHERE employeeinfo.id = operation_info.operid), operation_info.operid) AS name, operation_info.opertrash
FROM
operation_info
WHERE
operday = #{operday}
AND
opernum = #{opernum}
ORDER BY
opertype;

delete.foreach:
DELETE FROM
operation_info
WHERE
operday = #{value.operday}
AND
opernum = #{value.opernum}
AND
operno = #{value.operno}
AND
opertype = #{value.opertype}
