mapper.update:
UPDATE
employeeinfo
SET
company = #{company}, kind = #{kind}, joind = #{joind}, endD = #{endd}, name = #{name}, gender = #{gender}, birthday = #{birthday}, phone1 = #{phone1}, phone2 = #{phone2}, address = #{address}, garage = #{garage}, bosuM = #{bosum}, bobuJ = #{bobuj}, drvL = #{drvl}, busL = #{busl}, memo = #{memo}, bank = #{bank}, gye = #{gye}, gyename = #{gyename}, baseM = #{basem}, kukM = #{kukm}, gunM = #{gunm}, goM = #{gom}, sanM = #{sanm}, trash = #{trash}, img = #{img}
WHERE
id = #{id};

mapper.select:
SELECT
FROM employeeinfo
LEFT JOIN vehicleinfo
ON employeeinfo.id = vehicleinfo.id
WHERE employeeinfo.name = #{name}
ORDER BY name;

SELECT
FROM employeeinfo
LEFT JOIN vehicleinfo
ON employeeinfo.id = vehicleinfo.id
WHERE employeeinfo.id = #{id}
ORDER BY name;
